// voici des champs : 
// 
// Nom - Veuillez saisir le nom du participant / Required / String
// Prénom - Veuillez saisir le prénom du participant / Required / String
// Email - Veuillez saisir l' email du participant / Not Required / Email
// Numéro de téléphone / Not Required / Number
// Sexe - Veuillez choisir le sexe du participant / Required / Checkbox (Homme, Femme)
// Wilaya - Veuillez choisir le département (wilaya) du participant - Les participants étrangers choisissent : 59 / Required / Select
// Avez-vous déjà une expérience dans le domaine de la protection du patrimoine ? / Required / Radio (Oui, Non)
// Si oui, décrivez votre parcours / Not Required / Textarea
// Pourquoi souhaitez vous participer à cet évènement ? (Vos motivation) / Not Required / Textarea
// Télécharger au moins 3 photos / Required / File (Extensions autorisées : jpg, jpeg, png)
// Bouton envoyer / Required / Submit


import React, { Component } from 'react';
//import './Formulaire.css';

class Formulaire extends Component {
    constructor(props) {
        super(props);
        this.state = {
        nom: '',
        prenom: '',
        email: '',
        tel: '',
        sexe: '',
        wilaya: '',
        experience: '',
        parcours: '',
        motivation: '',
        photos: [],
        errors: {
            nom: '',
            prenom: '',
            email: '',
            tel: '',
            sexe: '',
            wilaya: '',
            experience: '',
            parcours: '',
            motivation: '',
            photos: ''
        }
        };
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
        [name]: value
        });
    }

    handleFileChange = (event) => {
        const { name, files } = event.target;
        this.setState({
        [name]: files
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const { nom, prenom, email, tel, sexe, wilaya, experience, parcours, motivation, photos } = this.state;
        let errors = {
        nom: '',
        prenom: '',
        email: '',
        tel: '',
        sexe: '',
        wilaya: '',
        experience: '',
        parcours: '',
        motivation: '',
        photos: ''
        };

        if (!nom) {
        errors.nom = 'Veuillez saisir le nom du participant';
        }

        if (!prenom) {
        errors.prenom = 'Veuillez saisir le prénom du participant';
        }

        if (!sexe) {
        errors.sexe = 'Veuillez choisir le sexe du participant';
        }

        if (!wilaya) {
        errors.wilaya = 'Veuillez choisir le département (wilaya) du participant';
        }

        if (!experience) {
        errors.experience = 'Veuillez choisir si vous avez déjà une expérience dans le domaine de la protection du patrimoine';
        }

        if (!photos || photos.length < 3) {
        errors.photos = 'Veuillez télécharger au moins 3 photos';
        }

        if (Object.values(errors).some(err => err)) {
        this.setState({ errors });
        } else {
        console.log('Nom:', nom);
        console.log('Prénom:', prenom);
        console.log('Email:', email);
        console.log('Numéro de téléphone:', tel);
        console.log('Sexe:', sexe);
        console.log('Wilaya:', wilaya);
        console.log('Avez-vous déjà une expérience dans le domaine de la protection du patrimoine ?', experience);
        console.log('Si oui, décrivez votre parcours:', parcours);
        console.log('Pourquoi souhaitez vous participer à cet évènement ?', motivation);
        console.log('Télécharger au moins 3 photos:', photos);
        }
    }

    render() {
        const { nom, prenom, email, tel, sexe, wilaya, experience, parcours, motivation, photos, errors } = this.state;
        return (
        <form className="formulaire" onSubmit={this.handleSubmit}>
            <div className="form-group">
            <label htmlFor="nom">Nom</label>
            <input type="text" id="nom" name="nom" value={nom} onChange={this.handleChange} />
            {errors.nom && <span className="error">{errors.nom}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="prenom">Prénom</label>
            <input type="text" id="prenom" name="prenom" value={prenom} onChange={this.handleChange} />
            {errors.prenom && <span className="error">{errors.prenom}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" value={email} onChange={this.handleChange} />
            {errors.email && <span className="error">{errors.email}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="tel">Numéro de téléphone</label>
            <input type="tel" id="tel" name="tel" value={tel} onChange={this.handleChange} />
            {errors.tel && <span className="error">{errors.tel}</span>}
            </div>
            <div className="form-group">
            <label>Sexe</label>
            <div>
                <label>
                <input type="radio" name="sexe" value="Homme" checked={sexe === 'Homme'} onChange={this.handleChange} />
                Homme
                </label>
                <label>
                <input type="radio" name="sexe" value="Femme" checked={sexe === 'Femme'} onChange={this.handleChange} />
                Femme
                </label>
            </div>
            {errors.sexe && <span className="error">{errors.sexe}</span>}
            </div>
            <div className="form-group">
            <label>Wilaya</label>
            <select name="wilaya" value={wilaya} onChange={this.handleChange}>
                <option value="">Choisir une wilaya</option>
                <option value="01">Adrar</option>
                <option value="02">Chlef</option>
                <option value="03">Laghouat</option>
                <option value="04">Oum El Bouaghi</option>
                <option value="05">Batna</option>
                <option value="06">Béjaïa</option>
                <option value="07">Biskra</option>
                <option value="08">Béchar</option>
                <option value="09">Blida</option>
                <option value="10">Bouira</option>
                <option value="11">Tamanrasset</option>
                <option value="12">Tébessa</option>
                <option value="13">Tlemcen</option>
                <option value="14">Tiaret</option>
                <option value="15">Tizi Ouzou</option>

            </select>
            {errors.wilaya && <span className="error">{errors.wilaya}</span>}
            </div>
            <div className="form-group">
            <label>Avez-vous déjà une expérience dans le domaine de la protection du patrimoine ?</label>
            <div>
                <label>
                <input type="radio" name="experience" value="Oui" checked={experience === 'Oui'} onChange={this.handleChange} />
                Oui
                </label>
                <label>
                <input type="radio" name="experience" value="Non" checked={experience === 'Non'} onChange={this.handleChange} />
                Non
                </label>
            </div>
            {errors.experience && <span className="error">{errors.experience}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="parcours">Si oui, décrivez votre parcours</label>
            <textarea id="parcours" name="parcours" value={parcours} onChange={this.handleChange} />
            {errors.parcours && <span className="error">{errors.parcours}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="motivation">Pourquoi souhaitez vous participer à cet évènement ?</label>
            <textarea id="motivation" name="motivation" value={motivation} onChange={this.handleChange} />
            {errors.motivation && <span className="error">{errors.motivation}</span>}
            </div>
            <div className="form-group">
            <label htmlFor="photos">Télécharger au moins 3 photos</label>
            <input type="file" id="photos" name="photos" multiple accept="image/*" onChange={this.handleFileChange} />
            {errors.photos && <span className="error">{errors.photos}</span>}
            </div>
            <button type="submit">Envoyer</button>
        </form>
        );
    }
}

export default Formulaire;
