import React, { useState } from 'react';
import Formulaire from './Formulaire';

const Navbar = () => {
    const [activeComponent, setActiveComponent] = useState(<div>Bienvenue sur la page d'accueil</div>);

    const buttons = [
        { name: 'Accueil', callback: () => setActiveComponent(<div>Bienvenue sur la page d'accueil</div>) },
        { name: 'Formulaire', callback: () => setActiveComponent(<Formulaire />) },
        { name: 'Contact', callback: () => setActiveComponent(<Formulaire />) }
    ];

    return (
        <div>
            <nav style={{ display: 'flex', justifyContent: 'space-around', padding: '1rem', background: '#333', color: '#fff' }}>
                {buttons.map((button, index) => (
                    <button
                        key={index}
                        onClick={button.callback}
                        style={{ background: 'none', border: 'none', color: '#fff', cursor: 'pointer' }}
                    >
                        {button.name}
                    </button>
                ))}
            </nav>
            <div style={{ padding: '1rem' }}>
                {activeComponent}
            </div>
        </div>
    );
};

export default Navbar;
