import React, { useState } from 'react';
import Formulaire from './Formulaire';

const Navbar = () => {
    const [activeComponent, setActiveComponent] = useState(null);

    const renderComponent = () => {
        switch (activeComponent) {
            case 'formulaire':
                return <Formulaire />;
            default:
                return <div>Bienvenue sur la page d'accueil</div>;
        }
    };

    return (
        <div>
            <nav style={{ display: 'flex', justifyContent: 'space-around', padding: '1rem', background: '#333', color: '#fff' }}>
                <button onClick={() => setActiveComponent(null)} style={{ background: 'none', border: 'none', color: '#fff', cursor: 'pointer' }}>Accueil</button>
                <button onClick={() => setActiveComponent('formulaire')} style={{ background: 'none', border: 'none', color: '#fff', cursor: 'pointer' }}>Formulaire</button>
            </nav>
            <div style={{ padding: '1rem' }}>
                {renderComponent()}
            </div>
        </div>
    );
};

export default Navbar;
